        imgstring =
          imgstring +
          `<img src=${obj.goods_img[i]} style="width:58px;margin-left:5px">`
      }
      imgstring + "</span>"

      this.$Modal.info({
        // closable: true,
        title: "商品详情",
        // closable: true,
        content: `菜单名称：${obj.name}<br>
        商品ID：${obj.goods_id}<br>
        原价：${obj.price}<br>
        简短描述：<br>${obj.brief}<br>
        微缩图片：<br><img src="${obj.thumb}" style="width:58px;"><br>
        实际大小图片：<br>${imgstring}<br>
        是否开放销售：${filters.is_not(Number(obj.is_on_sale))}<br>
        商品的显示顺序：${obj.sort_order}<br>
        商家备注：${obj.seller_note}<br>
        是否是精品：${filters.is_not(Number(obj.is_best))}<br>
        是否热销：${filters.is_not(Number(obj.is_hot))}<br>
        商品的添加时间：${filters.formatDate(
            new Date(obj.add_time * 1000),
            "yyyy-MM-dd hh:mm:ss"
          )}<br>
        详细描述：${obj.desc}<br>
                  `
      })
    }
  }
}
</script>

